#include <iostream>
#include <string>
using namespace std;

int main()
{
    string h1 = "";
    string h2 = "";
    int division = 8;
    int matriz[8][8];// Crea la matriz
    int Match;
    int Delete;
    int Insert;
    int bandera;

    // Inicializa los valores

    for(int i = 0; i < 8; i++){
            for(int j = 0; j < 8; j++){
                        matriz[i][j] = 0;
        }
    }
    for(int i = 0; i < 8; i++){
        matriz[i][0] = i;
        cout << i;
    }
    cout << "hola" << endl;
    for(int j = 0; j < 8; j++){
        matriz[0][j] = j;
        cout << j;
    }
    // Evalua casos
    cout << endl << "fin :" ;
    for(int i = 1; i < 8; i++){
        for(int j = 1; j < 8; j++){
            if(h1[i - 1] != h2[j - 1]){
                cout << h1[i - 1]<< h2[j-1]<<endl;
                cout << "diferentes"<< endl;
                bandera = 1;
            }else {
                bandera = 0;
                cout << h1[i - 1]<< h2[j-1]<<endl;
                cout << "IGUALES"<< endl;}
            Match = matriz[i-1][j-1] + bandera;
            cout << "M: " << Match << endl;
            Delete = matriz[i - 1][j] + 1;
            cout << "D " <<Delete << endl;
            Insert = matriz[j][i - 1] + 1;
            cout << "I " << Insert << endl;
            matriz[i][j] = min(min(Match, Insert), Delete);
            cout << "min : " << matriz[i][j] << endl;

        }
    }

    return 0;
}
